version.script:
import org.efaps.admin.runlevel.RunLevel;
import org.efaps.db.Context;
import org.efaps.db.Insert;
import org.efaps.db.SearchQuery;
import org.efaps.esjp.admin.ui.UIUpdate;
import org.efaps.importer.DataImport;
import org.efaps.db.Instance;

import java.net.URL
import java.io.File;

EFAPS_LOGGER.info("Checking for import of JaasKeys for eFapsApp-HumanResource");

SearchQuery query = new SearchQuery();
query.setQueryTypes("Admin_User_JAASKey");
query.addWhereExprEqValue("Key", "HumanResource_Admin");
query.addSelect("OID");
query.execute();
if (!query.next()) {

    RunLevel.init("shell");
    RunLevel.execute();

    EFAPS_LOGGER.info("    Importing JaasKeys for eFapsApp-HumanResource");
    URL url = new URL(EFAPS_ROOTURL, "Data/IMPORT_UserJAASKey.xml");
    DataImport imp = DataImport.readFile(url);
    imp.updateInDB();
}

UIUpdate update = new UIUpdate();

update.add2Menu("95a2cfe0-92f0-432b-bbfb-6dee6e91a181", "87001cc3-c45c-44de-b8f1-776df507f268", 1);


update.add2Menu("c1d7896b-8a1c-4257-88e1-92c286de61b8", "450debe4-df03-4137-abd1-bfc3c8e4b2f1", 1);
